import React,{Component} from "react";

export default class DynamicClass3 extends Component{
    state={
        marksColor:["red", "orange", "yellow", "green", "violet"],
        percentage:0,
        result:"Invalid",
    }
    onCalculateHandler=(event)=>{
        this.setState({percentage:event.target.value})
    }
    onClickHandler=()=>{
        this.setState({result:this.calculateResult(this.state.percentage)}) 

     }
     calculateResult=(percentage)=>{
         let result="Invalid";
         if(percentage>=0 && percentage<35){
             result="Fail";
         }else if(percentage>=35 && percentage<=50){
             result="Just Pass";
         }else if(percentage>50 && percentage<=60){
             result="Second Pass";
         }else if(percentage>60 && percentage<=70){
             result="First Class";
         }else if(percentage>70 && percentage<=100){
             result="Distinction";
         }

     }
    render(){
        return(
            <>
            <h3>Put Your Percentage here:</h3>
            <input onChange={this.onInputChange}></input>
            <button onClick={this.onClickButton}>Check Result</button>
            <h1>You are:</h1>
            </>
        )
    }



}
