import React,{Component} from "react";


export default class FormComponent extends Component{
    state={
        name:"",
        age:0,
    };
    onInputNameChange=(event)=>{
        this.setState({name:event.target.value})
    }
    onInputAgeChange=(event)=>{
        this.setState({age:event.target.value})
    }
    onAdd=()=>{
        const myNameCopy=this.state.name;
        myNameCopy.push(this.state.age);
        this.setState({name:myNameCopy, age:""});
    };
    onFormSubmitHandler=(event)=>{
        event.preventDefault();
    }
    render(){
        return(
            <>
            <form onSubmit={this.onFormSubmitHandler}>
            <h1>Person Form</h1>
            <label>Name:</label>
            <input value={this.state.name} onChange={this.onInputNameChange}/>
            <br/>
            <label>Age::</label>
            <input value={this.state.age} onChange={this.onInputAgeChange}/>
            <br/>
            <button type="submit">Submit</button>
            </form>
            </>
        )
    }

}
