import React,{Component} from "react";
export default class PersonForm extends Component{
    state={
        name:"",
        age:0,
        mobile:0,

        myList:[],
    }
    onInputChange=(event)=>{
        this.setState({[event.target.name]:event.target.value})
        console.log(event.target.name);
    }
    onFormSubmitHandler=(event)=>{
        event.preventDefault();
        console.log("in submit");
        console.log(this.state);
    }
    onAdd=()=>{
        const myListCopy=this.state.myList;
        myListCopy.push(this.state.name, this.state.age, this.state.mobile);
        this.setState({myList:myListCopy, name:"", age:0, mobile:0});
    };



    render(){
        return(
            <>
            <h1>Person Form</h1>
            <form onSubmit={this.onFormSubmitHandler}>
                <label>Name:</label>
                <input name={"name"} value={this.state.name} onChange={this.onInputChange}></input>
                <br/>
                <label>Age:</label>
                <input name={"age"} value={this.state.age} onChange={this.onInputChange}></input>
                <br/>
                <label>Mobile No:</label>
                <input name={"mobile"} value={this.state.mobile} onChange={this.onInputChange}></input>
                <br/>
                <button type="submit" onClick={this.onAdd}>Submit</button>
            </form>
            <h1>My List</h1>
            {this.state.myList.map((element, index)=>(
            <div key={index}><div>{element}</div>
            <h3>Hi I'm {this.state.name} my age is{this.state.a} </h3>
            </div>

            ))}
            </>
        )
    }

}
