import React,{Component} from "react";

export default class DynamicClass3 extends Component{
    state={
        marksColor:["red", "orange", "yellow", "green", "violet"],
        inputMark:0,
        incrementer:0,
        result:"",
    }
    onClickButton=()=>{
        this.setState({incrementer:this.state.incrementer +1})
    }
    onInputChange=(event)=>{
        this.setState({inputMark:event.target.value}) 

     }
    onPercentageCriteria=()=>{
        if(this.inputMark<35){
            this.setState({result:"you are Failed"})
        }
    }
    render(){
        return(
            <>
            <h3>Put Your Percentage here:</h3>
            <input onChange={this.onInputChange}></input>
            <button onClick={this.onClickButton}>Check Result</button>
            <h1 style={{backgroundColor:this.state.marksColor[this.incrementer]}}>You are:</h1>
            </>
        )
    }



}
